/*!

=========================================================
* Paper Kit React - v1.2.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-kit-react

* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/paper-kit-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

// reactstrap components
import {
  Button,
  Label,
  FormGroup,
  Input,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import ProfilePageHeader from "components/Headers/ProfilePageHeader.js";
import Footer from "components/Footers/Footer.js";

function ProfilePage() {
  const [activeTab, setActiveTab] = React.useState("1");

  const toggle = (tab) => {
    if (activeTab !== tab) {
      setActiveTab(tab);
    }
  };

  document.documentElement.classList.remove("nav-open");
  React.useEffect(() => {
    document.body.classList.add("landing-page");
    return function cleanup() {
      document.body.classList.remove("landing-page");
    };
  });
  return (
    <>
      <ExamplesNavbar />
      <ProfilePageHeader />
      <div className="section profile-content">
        <Container>
          <div className="owner">
            <div className="avatar">
              <img
                alt="..."
                className="img-circle img-no-padding img-responsive"
                src={require("assets/img/faces/ruben-barreiro.png")}
              />
            </div>
            <div className="name">
              <h4 className="title">
                <b>Rúben André Barreiro</b>
                <br />
              </h4>
              <h6 className="description">Computer Scientist/Engineer and Master Student</h6>
            </div>
          </div>
          <Row>
            <Col className="ml-auto mr-auto text-center" md="6">
                
              <hr />
                
              <p>
                I'm a <i>hard-working</i> and <i>passionate</i> <b>Computer Scientist and Engineer</b> with <i>huge dreams</i>. I'm currently finishing the <b>Master</b> at <a href="https://www.fct.unl.pt/en" target="_blank"><b>Faculty of Sciences and Technology of New University of Lisbon (FCT NOVA)</b></a>.
              </p>
              <br />
              <p>
                I have a <i>huge eagerness to learn and improve myself everyday</i>, <i>professionally</i> and <i>personally</i>.
              </p>
              <br />
              <p>
                I have a deep passion for IT and Technologies Areas.
              </p>
              <br />
              <p>
                I'm strongly motivated by my goals and ambitions, never giving up of them and I'm easy to get along with.
              </p>
              <br />
              <p>
                In Computer Science and Engineering, I like Logic, Computer Networks, Computer Graphics, Artificial Intelligence, Distributed Systems, Edge/Fog Computing, Cloud Computing, Network & Internet Security, Cryptography, Concurrent & Parallel Systems, High Performance Computing, GPU Programming, Machine Learning, Autonomous Multi-Agent Systems, Robotics and Quantum Computing.
              </p>

              <hr />
                    
              <h5><b>Social</b></h5>
            
              <br />
                  
              <Button className="btn-round" color="default" outline>
                <i className="fa fa-facebook" /> Facebook
              </Button>
              &nbsp;&nbsp;&nbsp;&nbsp;
              <Button className="btn-round" color="default" outline>
                <i className="fa fa-instagram" /> Instagram
              </Button>
              &nbsp;&nbsp;&nbsp;&nbsp;
              <Button className="btn-round" color="default" outline>
                <i className="fa fa-cog" /> Twitter
              </Button>
            </Col>
          </Row>
          <br />
          <div className="nav-tabs-navigation">
            <div className="nav-tabs-wrapper">
              <Nav role="tablist" tabs>
                <NavItem>
                  <NavLink
                    className={activeTab === "1" ? "active" : ""}
                    onClick={() => {
                      toggle("1");
                    }}
                  >
                    Personal Info
                  </NavLink>
                  <NavLink
                    className={activeTab === "2" ? "active" : ""}
                    onClick={() => {
                      toggle("2");
                    }}
                  >
                    Follows
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={activeTab === "3" ? "active" : ""}
                    onClick={() => {
                      toggle("3");
                    }}
                  >
                    Following
                  </NavLink>
                </NavItem>
              </Nav>
            </div>
          </div>
          {/* Tab panes */}
          <TabContent className="following" activeTab={activeTab}>
            
          </TabContent>
        </Container>
      </div>
      <Footer />
    </>
  );
}

export default ProfilePage;
